                          <p className="text-xl font-bold text-blue-600" data-testid="text-tva-amount">
                            {simulation.taxes.tva.toLocaleString('fr-FR', { 
                              style: 'currency', 
                              currency: 'EUR' 
                            })}
                          </p>
